import React, { Fragment, useEffect, useState } from "react";
import { toast, Slide } from 'react-toastify';

import { addToCart } from "../../../utils/cartFunctions";
import convertNameToImage from "../../../utils/convertNameToImage";

import '../ProductDescription.css';

const DrinkDescription = ({ product, cartNumChange }) => {
    const [productSize, setProductSize] = useState("M");
    const [productPrice, setProductPrice] = useState(product.medium_price);
    const productName = product.product_name;
    const productId = product.product_id;

    const setSize = (e) => {
        setProductSize(e.target.value);
    };

    const toggleProductInfo = (e) => {
        const previousActive = document.querySelectorAll(".active");
        if(previousActive.length !== 0) {
            for(let i = 0; i < previousActive.length; i++) {
                previousActive[i].classList.remove("active");
            }
        }
        const DrinkDescription = document.getElementById(`${productId}-description`);
        DrinkDescription.classList.add("active");
        const productSelections = DrinkDescription.querySelector(".product-selections");
        productSelections.classList.add("active");
    };

    const addToOrder = async () => {
        const success = await addToCart(productId, productName, productSize, cartNumChange);
        if(success) {
            toast.dismiss();
            toast(`${productName} Added to Your Cart`, {
                position: "bottom-center",
                autoClose: 5000,
                hideProgressBar: true,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                transition: Slide
            });
        }
    };

    useEffect(() => {
        switch(productSize) {
            case "S":
                setProductPrice(product.small_price);
                break;
            case "M":
                setProductPrice(product.medium_price);
                break;
            case "L":
                setProductPrice(product.large_price);
                break;
            default:
                break;
        }
    }, [productSize, product]);

    return (
        <div className="product-description" id={`${productId}-description`}>
            <div className="product-info" id={`${productId}-info`}>
                <button className="product-button" onClick={toggleProductInfo}>
                    <div className="product-name-and-image">
                        <img className="product-image" alt="" src={require(`../../../images/drinks/${convertNameToImage(productName)}`)} />
                        <h4 className="product-name">{productName}</h4>
                    </div>
                </button>
                <div className="product-selections" id={`${productId}-selections`}>
                    <form className="size-selector">
                            <div>
                                <label htmlFor="Small">Small</label>
                                <input id="Small" name="size" type="radio" value="S" onClick={setSize} />
                            </div>
                            <div>
                                <label htmlFor="Medium">Medium</label>
                                <input id="Medium" name="size" type="radio" value="M" onClick={setSize} defaultChecked/>
                            </div>
                            <div>
                                <label htmlFor="Large">Large</label>
                                <input id="Large" name="size" type="radio" value="L" onClick={setSize} />
                            </div>
                    </form>
                    <div className="price-and-btn">
                        <div className="product-price">Price: $<span>{productPrice}</span></div>
                        <button className="btn btn-white btn-animate" onClick={addToOrder}>Add To Order</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default DrinkDescription;
