const getNumInCart = async () => {
    const response = await fetch(`http://localhost:3001/cart`, {
        method: "GET",
        credentials: "include"
    });
    if(response.ok) {
        const parseRes = await response.json();
        let num = 0;
        parseRes.forEach(cartItem => {
            num += cartItem.quantity;
        });
        return num;
    }
};

export { getNumInCart };
