import React, { Fragment, useState } from "react";

const Register = () => {
    const [inputs, setInputs] = useState({
        first_name: "",
        last_name: "",
        email: "",
        phone: "",
        password: ""
    });

    const {first_name, last_name, email, phone, password} = inputs;

    return (
        <Fragment>
            <h1 className="text-center my-5">Register</h1>
            <form>
                <input type="text" name="first_name" placeholder="First Name" className="form-control my-3" />
                <input type="text" name="last_name" placeholder="Last Name" className="form-control my-3" />
                <input type="tel" name="phone" pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" placeholder="Phone Number" required className="form-control my-3" />
                <input type="email" name="email" placeholder="Email" className="form-control my-3" />
                <input type="password" name="password" placeholder="Password" className="form-control my-3" />
                <button className="btn btn-success btn-block">Submit</button>
            </form>
        </Fragment>
    );
};

export default Register;
