import React, { Fragment, useEffect, useState } from "react";
import MenuCategories from "../MenuCategories";
import { useParams } from "react-router-dom";
import '../Menu.css';
import FoodDescription from "./FoodDescription";

const FoodMenu = () => {
    const [foods, setFoods] = useState([]);
    const [foodCategory, setFoodCategory] = useState("");
    const { foodType } = useParams();

    useEffect(() => {
        const getFoods = async () => {
            const response = await fetch(`http://localhost:3001/menu/food/${foodType}`, {
                method: "GET",
                credentials: "include"
            });
            if(response.ok) {
                const parseRes = await response.json();
                setFoods(parseRes);
            }
        }
        getFoods();

        const foodCategories = {
            "hot-breakfast": "Hot Breakfast",
            "bakery": "Bakery",
            "lunch": "Lunch"
        };
        setFoodCategory(foodCategories[foodType]);

    }, [foodType]);

    return (
        <Fragment>
            <h1>Menu</h1>
            <div className="menu-with-nav">
                <MenuCategories />
                <div className="menu-container">
                    <nav className="breadcrumbs">
                        <ul>
                            <li><a href="/menu">Menu</a><span>&nbsp;/&nbsp;</span></li>
                            <li><span className="current-page">{foodCategory}</span></li>
                        </ul>
                    </nav>
