import React, { Fragment, useState } from "react";

const ProductDescription = ({ drink }) => {
    const [drinkSize, setDrinkSize] = useState("");

    return (
        <Fragment>
            <div className="product-description">
                <h4>{drink.drink_name}</h4>
                <form className="size-selector">
                    <label for="Small">Small</label>
                    <input id="Small" name="size" type="radio" value="Small" />
                    <input id="Medium" name="size" type="radio" value="Medium" />
                    <input id="Large" name="size" type="radio" value="Large" />
                </form>
            </div>
        </Fragment>
    );
};

export default ProductDescription;
