import React, { Fragment, useEffect, useState } from "react";
import MenuCategories from "../../components/Menu/MenuCategories";
import { useParams } from "react-router-dom";
import ProductDescription from "../../components/Menu/ProductDescription";
import './ProductMenu.css';

const ProductMenu = ({ cartNumChange }) => {
    const [products, setProducts] = useState([]);
    const { productType } = useParams();

    const productCategories = {
        "hot-coffees": "Hot Coffees",
        "cold-coffees": "Cold Coffees",
        "sparklers": "Sparklers",
        "frozen-coffees": "Frozen Coffees",
        "hot-teas": "Hot Teas",
        "iced-teas": "Iced Teas",
        "milk-juice": "Milk & Juices",
        "hot-breakfast": "Hot Breakfast",
        "bakery": "Bakery",
        "lunch": "Lunch"
    };
    const productCategory = productCategories[productType];


    useEffect(() => {
        const getProducts = async () => {
            let fetchURL = 'http://localhost:3001/menu/';
            if(window.location.href.includes("drinks")) {
                fetchURL += `drinks/${productType}`;
            } else if(window.location.href.includes("food")) {
                fetchURL += `food/${productType}`;
            }
            const response = await fetch(fetchURL, {
                method: "GET",
                credentials: "include"
            });
            if(response.ok) {
                const parseRes = await response.json();
                setProducts(parseRes);
            }
        }
        getProducts();
    }, [productType]);

    return (
        <Fragment>
            <h1>Menu</h1>
            <div className="menu-with-nav">
                <MenuCategories />
                <div className="menu-container">
                    <nav className="breadcrumbs">
                        <ul>
                            <li><a href="/menu">Menu</a><span>&nbsp;/&nbsp;</span></li>
                            <li><span className="current-page">{productCategory}</span></li>
                        </ul>
                    </nav>
                    <h2>{productCategory}</h2>
                    <div className="products-container">
                        {
                            products.map((product, index) => {
                                return (
                                    <div className="product-container" key={index}>
                                        <ProductDescription product={product} cartNumChange={cartNumChange} />
                                    </div>
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default ProductMenu;
