import React, { Fragment, useState, useEffect } from "react";
import "./Cart.css";

const Cart = () => {
    const [cartItems, setCartItems] = useState([]);

    const getCartItems = async () => {
        const response = await fetch(`http://localhost:3001/cart`, {
            method: "GET",
            credentials: "include"
        });
        if(response.ok) {
            const parseRes = await response.json();
            setCartItems(parseRes);
        }
    };

    useEffect(() => {
        getCartItems();
    });

    return (
        <Fragment>
            <h1>Cart</h1>
            {
                cartItems.map(cartItem => {
                    return (
                        <div className="cart-item-container">
                            <h3>{cartItem.product_name}</h3>
                            <div>
                                { cartItem.product_size === "S" && (
                                    <p>Size: <span>Small</span></p>
                                )}
                                { cartItem.product_size === "M" && (
                                    <p>Size: <span>Medium</span></p>
                                )}
                                { cartItem.product_size === "L" && (
                                    <p>Size: <span>Large</span></p>
                                )}
                                <p>Quantity: <span>{cartItem.quantity}</span></p>
                            </div>
                            <h4>Price: $<span>{cartItem.total_price}</span></h4>
                        </div>
                    );
                })
            }
        </Fragment>
    );
};

export default Cart;
