import React, { Fragment } from "react";

const CartItem = ({ cartItem }) => {
    const addToCart = async () => {
        const body = {
            "product_id": cartItem.product_id, 
            "product_size": cartItem.product_size, 
            "quantity": 1
        };
        const response = await fetch(`http://localhost:3001/cart/add-to-cart`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            await response.json();
            // alert("Item successfully added to cart!");
        } else if(response.status === 403) {
            alert("You must be logged in to add items to your cart!");
        }
    };

    const timeout = (ms) => {
        return new Promise(resolve => setTimeout(resolve, ms));
    };

    const removeFromCart = async () => {
        if((cartItem.quantity - 1) === 0) {
            document.getElementById(`${cartItem.product_id}-${cartItem.product_size}-container`).classList.add("fadeOut");
            await timeout(1000);
        }
        const body = {
            "product_id": cartItem.product_id, 
            "product_size": cartItem.product_size, 
            "quantity": 1
        };
        const response = await fetch(`http://localhost:3001/cart/remove-from-cart`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            await response.json();
            // alert("Item successfully remove from your cart.");
        } else if(response.status === 403) {
            alert("You must be logged in to remove items from your cart!");
        }
    };

    const updateCartItemAmount = async (e) => {
        if(e.target.value === "") return;
        if(e.target.value === 0) {
            document.getElementById(`${cartItem.product_id}-${cartItem.product_size}-container`).classList.add("fadeOut");
            await timeout(1000);
        }
        const body = {
            "product_id": cartItem.product_id, 
            "product_size": cartItem.product_size, 
            "quantity": e.target.value
        };
        const response = await fetch(`http://localhost:3001/cart/update-quantity`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            await response.json();
            // alert("Quantity successfully updated in your cart.");
        } else if(response.status === 403) {
            alert("You must be logged in to add/remove items from your cart!");
        }
    };

    return (
        <Fragment>
            <div className="cart-item-description">
                <div className="product-name-size">
                    <div className="product-name">
                        <h3>{cartItem.product_name}</h3>
                    </div>
                    <div className="product-size">
                        { cartItem.product_size === "S" && (
                            <p>Size: <span>Small</span></p>
                        )}
                        { cartItem.product_size === "M" && (
                            <p>Size: <span>Medium</span></p>
                        )}
                        { cartItem.product_size === "L" && (
                            <p>Size: <span>Large</span></p>
                        )}
                    </div>
                </div>
                <div className="quantity-container">
                    <button onClick={removeFromCart}>-</button>
                    <input type="number" value={cartItem.quantity} onChange={updateCartItemAmount}/>
                    <button onClick={addToCart}>+</button>
                </div>
                <div className="total-price">
                    <h4>Price: $<span>{cartItem.total_price}</span></h4>
                </div>
            </div>
        </Fragment>
    );
};

export default CartItem;
