import React, { Fragment, useEffect, useState } from "react";
import './DrinkDescription.css';

const DrinkDescription = ({ drink, cartNumChange }) => {
    const [drinkSize, setDrinkSize] = useState("M");
    const [drinkPrice, setDrinkPrice] = useState(drink.drink_medium_price);

    const setSize = (e) => {
        setDrinkSize(e.target.value);
    };

    const toggleProductInfo = (e) => {
        const previousActive = document.querySelectorAll(".active");
        if(previousActive.length !== 0) {
            for(let i = 0; i < previousActive.length; i++) {
                previousActive[i].classList.remove("active");
            }
        }
        const productDescription = document.getElementById(`${drink.drink_id}-description`);
        productDescription.classList.add("active");
        const productSelections = productDescription.querySelector(".product-selections");
        productSelections.classList.add("active");
    };

    const addToOrder = async (e) => {
        e.preventDefault();

        const body = {
            "product_id": drink.drink_id, 
            "product_size": drinkSize, 
            "quantity": 1
        };
        const response = await fetch(`http://localhost:3001/cart/add-to-cart`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            await response.json();
            alert("Item added successfully to your cart!");
        } else if(response.status === 403) {
            alert("You must be logged in to add items to your cart!");
        }
        cartNumChange(1);
    };

    useEffect(() => {
        switch(drinkSize) {
            case "S":
                setDrinkPrice(drink.drink_small_price);
                break;
            case "M":
                setDrinkPrice(drink.drink_medium_price);
                break;
            case "L":
                setDrinkPrice(drink.drink_large_price);
                break;
            default:
                break;
        }
    }, [drinkSize, drink]);

    return (
        <Fragment>
            <div className="product-description" id={`${drink.drink_id}-description`}>
                <div className="product-info" id={`${drink.drink_id}-info`}>
                    <h4><button className="drink-name" onClick={toggleProductInfo}>{drink.drink_name}</button></h4>
                    <div className="product-selections" id={`${drink.drink_id}-selections`}>
                        <form className="size-selector">
                            <div>
                                <label htmlFor="Small">Small</label>
                                <input id="Small" name="size" type="radio" value="S" onClick={setSize} />
                            </div>
                            <div>
                                <label htmlFor="Medium">Medium</label>
                                <input id="Medium" name="size" type="radio" value="M" onClick={setSize} defaultChecked/>
                            </div>
                            <div>
                                <label htmlFor="Large">Large</label>
                                <input id="Large" name="size" type="radio" value="L" onClick={setSize} />
                            </div>
                        </form>
                        <h5>Price: $<span className="drink-price">{drinkPrice}</span></h5>
                        <button className="btn btn-white btn-animate" onClick={addToOrder}>Add To Order</button>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default DrinkDescription;
