import React, { useState } from "react";
import { Link } from "react-router-dom";
import { timeout } from "../../utils/timeout";

import "./Auth.css";

const Register = ({ setAuth }) => {
    const [inputs, setInputs] = useState({
        first_name: "",
        last_name: "",
        email: "",
        phone: "",
        password: ""
    });

    const {first_name, last_name, email, phone, password} = inputs;

    const onChange = (e) => {
        setInputs({
            ...inputs, 
            [e.target.name] : e.target.value
        });
    };

    const addLabelClasses = (e) => {
        const labelForElem = document.querySelector("label[for=" + e.target.name + "]");
        labelForElem.classList.add("float-label--isActive");
    };

    const removeLabelClasses = async (e) => {
        const labelForElem = document.querySelector("label[for=" + e.target.name + "]");
        labelForElem.classList.remove("float-label--isActive");
        labelForElem.classList.add("float-label--isClosing");
        await timeout(250);
        labelForElem.classList.remove("float-label--isClosing");
    };

    const onSubmitForm = async (e) => {
        e.preventDefault();

        try {
            const body = {first_name, last_name, email, phone, password};
            const response = await fetch("http://localhost:3001/auth/register", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body),
                credentials: "include"
            });
            if(response.ok) {
                await response.json();
                setAuth(true);
            } else if(response.status === 401) {
                document.getElementById("register-user-exists").style.display = "block";
            } else {
                document.getElementsByClassName("login-server-error").style.display = "block";
            }
        } catch(err) {
            console.error(err.message);
        }
    };

    return (
        <>
            <h1 className="text-center my-5">Register</h1>
            <form onSubmit={onSubmitForm}>
                <input 
                    type="text" 
                    name="first_name" 
                    placeholder="First Name" 
                    className="form-control my-3"
                    value={first_name}
                    onChange={e => onChange(e)}
                />
                <input 
                    type="text" 
                    name="last_name" 
                    placeholder="Last Name" 
                    className="form-control my-3" 
                    value={last_name}
                    onChange={e => onChange(e)}
                />
                <input 
                    type="tel" 
                    name="phone" 
                    pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}" 
                    placeholder="Phone Number" 
                    required 
                    className="form-control my-3" 
                    value={phone}
                    onChange={e => onChange(e)}
                />
                <input 
                    type="email" 
                    name="email" 
                    placeholder="Email" 
                    className="form-control my-3" 
                    value={email}
                    onChange={e => onChange(e)}
                />
                <input 
                    type="password" 
                    name="password" 
                    placeholder="Password" 
                    className="form-control my-3" 
                    value={password}
                    onChange={e => onChange(e)}
                />
                <button className="btn btn-success btn-block">Register</button>
            </form>
            <p id="register-user-exists" style={{color: 'red', display: 'none'}}>A user with that email already exists.</p>
            <p className="server-error" style={{color: 'red', display: 'none'}}>Server error. Please try again later.</p>
            <Link to="/Login">Login</Link>
        </>
    );
};

export default Register;
