import React, { Fragment, useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = ({ setAuth }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");

    const getName = async () => {
        try {
            const response = await fetch("http://localhost:3001/dashboard/", {
                method: "GET",
                credentials: "include"
            });
            const parseRes = await response.json();
            setFirstName(parseRes.user_first_name);
            setLastName(parseRes.user_last_name);
        } catch(err) {
            console.error(err.message);
        }
    };

    const logout = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3001/dashboard/logout", {
                method: "GET",
                credentials: "include"
            });
            await response.json();
            setFirstName("");
            setLastName("");
            setAuth(false);
        } catch(err) {
            console.error(err.message);
        }
    }

    const getGreeting = () => {
        const curHour = (new Date()).getHours();
        if(curHour < 12) {
            return "Morning";
        } else if(curHour < 18) {
            return "Afternoon";
        } else {
            return "Evening";
        }
    };

    useEffect(() => {
        getName();
    }, []);

    return (
        <Fragment>
            <div className="dashboard-container">
                <div className="dash-intro">
                    <h1 className="dash-greeting">{`Good ${getGreeting()}, ${firstName}!`}</h1>
                    <div>What would you like to do today?</div>
                </div>
                <div className="">
                    <a href="/menu" className="order-card-link">
                        <div className="order-card">
                            <div className="order-card-content">
                                <div className="order-card-title">Place An Order</div>
                                <div className="">Select your items, pay, and pick it up!</div>
                            </div>
                            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-caret-right-fill" viewBox="0 0 16 16">
                                <path d="m12.14 8.753-5.482 4.796c-.646.566-1.658.106-1.658-.753V3.204a1 1 0 0 1 1.659-.753l5.48 4.796a1 1 0 0 1 0 1.506z"/>
                            </svg>
                        </div>
                    </a>
                    <div className="orders-dash-section">
                        <div className="">Your Orders</div>
                        <div className="">View, track, cancel an order, or purchase again</div>
                    </div>
                    <div className="">
                        <div className="">Login & Security</div>
                        <div className="">Change login, name, and other sensitive information</div>
                    </div>
                    <div className="">
                        <div className="">Customer Service</div>
                        <div className="">View FAQs, help articles, or speak with a member of our team</div>
                    </div>
                </div>
                <button 
                    className="btn btn-primary"
                    onClick={e => logout(e)}
                >
                    Logout
                </button>
            </div>
        </Fragment>
    );
};

export default Dashboard;
