import React, { useState, useEffect } from "react";
import { Nav, Navbar as NavbarBs } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { Bag } from 'phosphor-react';

import './Navbar.css';

const Navbar = ({ setAuth, numInCart }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    const isAuth = async () => {
        try {
        const response = await fetch("http://localhost:3001/auth/is-verify", {
            method: "GET",
            credentials: "include"
        });
        const parseRes = await response.json();
        parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false);
        } catch(err) {
        console.error(err.message);
        }
    };

    const logout = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3001/dashboard/logout", {
                method: "GET",
                credentials: "include"
            });
            await response.json();
            setAuth(false);
            setIsAuthenticated(false);
        } catch(err) {
            console.error(err.message);
        }
    }

    const setActiveLink = () => {
        const previousActive = document.querySelectorAll(".navbar-link-active");
        if(previousActive.length !== 0) {
            for(let i = 0; i < previousActive.length; i++) {
                previousActive[i].classList.remove("navbar-link-active");
            }
        }
    }

    useEffect(() => {
        isAuth();
        setActiveLink();
    });

    return (
        <NavbarBs className="bg-white shadow-sm mb-3 gutter-padding">
            <Nav className="me-auto" style={{alignItems: "center"}}>
                <img className="navbar-logo" alt="" src="../../petal-reverie-logo.png"/>
                <Nav.Link id="home-nav-link" className="navbar-link" to="/" as={NavLink}>Home</Nav.Link>
                <Nav.Link id="menu-nav-link" className="navbar-link" to="/menu" as={NavLink}>Menu</Nav.Link>
                <Nav.Link id="about-nav-link" className="navbar-link" to="/about" as={NavLink}>About</Nav.Link>
            </Nav>
            { !isAuthenticated && (
                <Nav>
                    <Nav.Link to="/login" as={NavLink}>Login</Nav.Link>
                    <Nav.Link to="/register" as={NavLink}>Register</Nav.Link>
                </Nav>
            )}
            { isAuthenticated && (
                <Nav style={{alignItems: "center"}}>
                    <Nav.Link to="/cart" as={NavLink} style={{ position: "relative" }} className="rounded-circle">
                        <Bag size={28}></Bag>
                        <div 
                            className="rounded-circle bg-danger d-flex justify-content-center align-items-center" 
                            id="amount-in-bag"
                            style={{
                                color: "white",
                                width: "1.25rem",
                                height: "1.25rem",
                                position: "absolute",
                                bottom: 0,
                                right: 0
                            }}
                        >{numInCart}</div>
                    </Nav.Link>
                    <Nav.Link to="/dashboard" as={NavLink}>Dashboard</Nav.Link>
                    <Nav.Link to="/home" onClick={logout}>Logout</Nav.Link>
                </Nav>
            )}
        </NavbarBs>
    );
};

export default Navbar;
