import React, {Fragment, useState, useEffect} from 'react';
import './App.css';

import { 
  BrowserRouter as Router, 
  Routes,
  Route, 
  Navigate
} from "react-router-dom";

//components

import Dashboard from './components/Dashboard';
import Login from './components/Login';
import Register from './components/Register';
import Menu from './components//Menu/Menu';
import DrinkMenu from './components/Menu/DrinkMenu/DrinkMenu';
import FoodMenu from './components/Menu/FoodMenu/FoodMenu';
import Home from './components/Home';
import Navbar from './components/Navbar';
import About from './components/About';
import Cart from './components/Cart/Cart';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [numInCart, setNumInCart] = useState(0);

  const setAuth = boolean => {
    setIsAuthenticated(boolean);
  };

  const isAuth = async () => {
    try {
      const response = await fetch("http://localhost:3001/auth/is-verify", {
        method: "GET",
        credentials: "include"
      });
      const parseRes = await response.json();
      parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false);
    } catch(err) {
      console.error(err.message);
    }
  };

  const cartNumChange = num => {
    setNumInCart(cartNum => cartNum + num);
  };

  const getNumInCart = async () => {
    if(isAuthenticated) {
        const response = await fetch(`http://localhost:3001/cart`, {
            method: "GET",
            credentials: "include"
        });
        if(response.ok) {
            const parseRes = await response.json();
            let num = 0;
            parseRes.forEach(cartItem => {
                num += cartItem.quantity;
            });
            setNumInCart(num);
        }
    }
  };

  useEffect(() => {
    isAuth();
    getNumInCart();
  });

  return (
    <Fragment>
      <Router>
        <Navbar numInCart={numInCart}/>
        <div className="container">
          <Routes>
            <Route 
              path="/"
              element={<Home />}
            />
            <Route 
              path="/about"
              element={<About />}
            />
            <Route 
              path="/login" 
              element={
                !isAuthenticated ? (
                  <Login setAuth={setAuth} />
                ) : (
                  <Navigate to="/dashboard" />
                )
              }
            />
            <Route 
              path="/register" 
              element={
                !isAuthenticated ? (
                  <Register setAuth={setAuth}/>
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route 
              path="/dashboard" 
              element={
                isAuthenticated ? (
                  <Dashboard setAuth={setAuth}/>
                ) : (
                  <Navigate to="/login" />
                )
              }
            />
            <Route 
              path="/menu" 
              element={<Menu cartNumChange={cartNumChange} />}
            />
            <Route 
              path="/cart"
              element={
                !isAuthenticated ? (
                  <Menu cartNumChange={cartNumChange}/>
                ) : (
                  <Cart cartNumChange={cartNumChange}/>
                )
              }
            />
            <Route path="/menu/drinks/:drinkType" element={<DrinkMenu />}/>
            <Route path="/menu/food/:foodType" element={<FoodMenu />}/>
          </Routes>
        </div>
      </Router>
    </Fragment>
  );
}

export default App;
