import React, { Fragment, useEffect, useState } from "react";
import MenuCategories from "../MenuCategories";
import { useParams } from "react-router-dom";
import '../Menu.css';
import DrinkDescription from "./DrinkDescription";

const DrinkMenu = ({ cartNumChange }) => {
    const [drinks, setDrinks] = useState([]);
    const [drinkCategory, setDrinkCategory] = useState("");
    const { drinkType } = useParams();

    useEffect(() => {
        const getDrinks = async () => {
            const response = await fetch(`http://localhost:3001/menu/drinks/${drinkType}`, {
                method: "GET",
                credentials: "include"
            });
            if(response.ok) {
                const parseRes = await response.json();
                setDrinks(parseRes);
            }
        }
        getDrinks();

        const drinkCategories = {
            "hot-coffees": "Hot Coffees",
            "cold-coffees": "Cold Coffees",
            "sparklers": "Sparklers",
            "frozen-coffees": "Frozen Coffees",
            "hot-teas": "Hot Teas",
            "iced-teas": "Iced Teas",
            "milk-juice": "Milk & Juices"
        };
        setDrinkCategory(drinkCategories[drinkType]);

    }, [drinkType]);

    return (
        <Fragment>
            <h1>Menu</h1>
            <div className="menu-with-nav">
                <MenuCategories />
                <div className="menu-container">
                    <nav className="breadcrumbs">
                        <ul>
                            <li><a href="/menu">Menu</a><span>&nbsp;/&nbsp;</span></li>
                            <li><span className="current-page">{drinkCategory}</span></li>
                        </ul>
                    </nav>
                    <h2>{drinkCategory}</h2>
                    <div className="products-container">
                        {
                            drinks.map((drink) => {
                                return (
                                    <div className="product-container" key={drink.drink_id}>
                                        <DrinkDescription drink={drink} cartNumChange={cartNumChange} />
                                    </div>
                                );
                            })
                        }
                    </div>
                </div>
            </div>
        </Fragment>
    );
}

export default DrinkMenu;
