import React, { Fragment, useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = ({ setAuth }) => {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");

    const getName = async () => {
        try {
            const response = await fetch("http://localhost:3001/dashboard/", {
                method: "GET",
                credentials: "include"
            });
            const parseRes = await response.json();
            setFirstName(parseRes.user_first_name);
            setLastName(parseRes.user_last_name);
        } catch(err) {
            console.error(err.message);
        }
    };

    const logout = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch("http://localhost:3001/dashboard/logout", {
                method: "GET",
                credentials: "include"
            });
            await response.json();
            setFirstName("");
            setLastName("");
            setAuth(false);
        } catch(err) {
            console.error(err.message);
        }
    }

    const getGreeting = () => {
        const curHour = (new Date()).getHours();
        if(curHour < 12) {
            return "Morning";
        } else if(curHour < 18) {
            return "Afternoon";
        } else {
            return "Evening";
        }
    };

    useEffect(() => {
        getName();
    }, []);

    return (
        <Fragment>
            <div className="dashboard-container">
                <h1 className="dash-greeting">{`Good ${getGreeting()}, ${firstName}!`}</h1>
                <div className="dash-options">
                    <a href="">
                        <div className="dash-option-card">
                            <div className="dash-card-title">Your Orders</div>
                            <div className="dash-card-desc">View, track, cancel an order, or purchase again</div>
                        </div>
                    </a>
                    <a href="">
                        <div className="dash-option-card">
                            <div className="dash-card-title">Login & Security</div>
                            <div className="dash-card-desc">Change login, name, and other sensitive information</div>
                        </div>
                    </a>
                    <a href="">
                        <div className="dash-option-card">
                            <div className="dash-card-title">Customer Service</div>
                            <div className="dash-card-desc">View FAQs, help articles, or speak with a member of our team</div>
                        </div>
                    </a>
                </div>
                <button 
                    className="btn btn-primary"
                    onClick={e => logout(e)}
                >
                    Logout
                </button>
            </div>
        </Fragment>
    );
};

export default Dashboard;
