import React, { Fragment } from "react";
import './FoodDescription.css';

const FoodDescription = ({ food }) => {
    const toggleProductInfo = (e) => {
        const previousActive = document.querySelectorAll(".active");
        if(previousActive.length !== 0) {
            for(let i = 0; i < previousActive.length; i++) {
                previousActive[i].classList.remove("active");
            }
        }
        const productDescription = document.getElementById(`${food.food_id}-description`);
        productDescription.classList.add("active");
        const productSelections = productDescription.querySelector(".product-selections");
        productSelections.classList.add("active");
    };

    const addToOrder = async (e) => {
        e.preventDefault();

        const body = {
            "product_id": food.food_id, 
            "product_size": "F", 
            "quantity": 1
        };
        const response = await fetch(`http://localhost:3001/cart/add-to-cart`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            await response.json();
            alert("Item added successfully to your cart!");
        } else if(response.status === 403) {
            alert("You must be logged in to add items to your cart!");
        }
    };

    return (
        <Fragment>
            <div className="product-description" id={`${food.food_id}-description`}>
                <div className="product-info" id={`${food.food_id}-info`}>
                    <h4><button className="food-name" onClick={toggleProductInfo}>{food.food_name}</button></h4>
                    <div className="product-selections" id={`${food.food_id}-selections`}>
                        <h5>Price: $<span className="food-price">{food.food_price}</span></h5>
                        <button onClick={addToOrder}>Add To Order</button>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default FoodDescription;
