import React, { Fragment, useEffect, useState } from "react";
import './ProductDescription.css';

const ProductDescription = ({ drink }) => {
    const [drinkSize, setDrinkSize] = useState("");
    const [drinkPrice, setDrinkPrice] = useState("0.00");

    const setSize = (e) => {
        setDrinkSize(e.target.value);
    };

    const toggleProductInfo = (e) => {
        const activeDrink = document.getElementsByClassName("active-drink");
        if(activeDrink.length !== 0) {
            activeDrink[0].style.display = "none";
        }
        document.getElementById(drink.drink_id).classList.add("active-drink");
    };

    const addToOrder = (e) => {
        e.preventDefault();

    };

    useEffect(() => {
        switch(drinkSize) {
            case "Small":
                setDrinkPrice(drink.drink_small_price);
                break;
            case "Medium":
                setDrinkPrice(drink.drink_medium_price);
                break;
            case "Large":
                setDrinkPrice(drink.drink_large_price);
                break;
            default:
                break;
        }
    }, [drinkSize, drink]);

    return (
        <Fragment>
            <div className="product-description" id={drink.drink_id}>
                <h4><button className="drink-name" onClick={toggleProductInfo}>{drink.drink_name}</button></h4>
                <div className="product-selections">
                    <form className="size-selector">
                        <label htmlFor="Small">Small</label>
                        <input id="Small" name="size" type="radio" value="Small" onClick={setSize} /><br />
                        <label htmlFor="Medium">Medium</label>
                        <input id="Medium" name="size" type="radio" value="Medium" onClick={setSize} /><br />
                        <label htmlFor="Large">Large</label>
                        <input id="Large" name="size" type="radio" value="Large" onClick={setSize} /><br />
                    </form>
                    <h5>Price: $<span className="drink-price">{drinkPrice}</span></h5>
                    <button onClick={addToOrder}>Add To Order</button>
                </div>
            </div>
        </Fragment>
    );
};

export default ProductDescription;
