import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";

import "./Login.css";

const Login = ({ setAuth }) => {
    const [inputs, setInputs] = useState({
        email: "",
        password: ""
    });

    const {email, password} = inputs;

    const onChange = (e) => {
        setInputs({
            ...inputs, 
            [e.target.name] : e.target.value
        });
    };

    const onSubmitForm = async (e) => {
        e.preventDefault();

        try {
            const body = {email, password};
            const response = await fetch("http://localhost:3001/auth/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(body),
                credentials: "include"
            });
            if(response.ok) {
                await response.json();
                setAuth(true);
            } else if(response.status === 401) {
                document.getElementById("incorrect-login").style.display = "block";
            } else {
                document.getElementById("login-server-error").style.display = "block";
            }
        } catch(err) {
            console.error(err.message);
        }
    };
    return (
        <Fragment>
            <div className="page-heading">Log in</div>
            <form onSubmit={onSubmitForm} className="auth-form">
                <div>* indicates a required field</div>
                <label className="float-label" for="email"><span>* </span> Email</label>
                <input 
                    type="email" 
                    name="email" 
                    placeholder="Email" 
                    value={email}
                    onChange={e => onChange(e)}
                />
                <label className="float-label" for="password"><span>* </span> Password</label>
                <input 
                    type="password" 
                    name="password" 
                    placeholder="Password"
                    value={password}
                    onChange={e => onChange(e)}
                />
                <button className="btn btn-success btn-block">Login</button>
            </form>
            <p id="incorrect-login" style={{color: 'red', display: 'none'}}>Incorrect email or password.</p>
            <p className="server-error" style={{color: 'red', display: 'none'}}>Server error. Please try again later.</p>
            <Link to="/register">Register</Link>
        </Fragment>
    );
};

export default Login;
