import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar as NavbarBs } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { Bag } from 'phosphor-react';

const Navbar = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [numInCart, setNumInCart] = useState(0);

    const isAuth = async () => {
        try {
        const response = await fetch("http://localhost:3001/auth/is-verify", {
            method: "GET",
            credentials: "include"
        });
        const parseRes = await response.json();
        parseRes === true ? setIsAuthenticated(true) : setIsAuthenticated(false);
        } catch(err) {
        console.error(err.message);
        }
    };

    const getNumInCart = async() => {
        if(isAuthenticated) {
            const response = await fetch(`http://localhost:3001/cart`, {
                method: "GET",
                credentials: "include"
            });
            if(response.ok) {
                const parseRes = await response.json();
                let num = 0;
                parseRes.forEach(cartItem => {
                    num += cartItem.quantity;
                });
                setNumInCart(num);
            }
        }
    };

    useEffect(() => {
        isAuth();
    });

    useEffect(() => {
        getNumInCart();
    });

    return (
        <NavbarBs className="bg-white shadow-sm mb-3">
            <Container>
                <Nav className="me-auto">
                    <Nav.Link to="/" as={NavLink}>Home</Nav.Link>
                    <Nav.Link to="/menu" as={NavLink}>Menu</Nav.Link>
                    <Nav.Link to="/about" as={NavLink}>About</Nav.Link>
                </Nav>
                { !isAuthenticated && (
                    <Nav>
                        <Nav.Link to="/login" as={NavLink}>Login</Nav.Link>
                        <Nav.Link to="/register" as={NavLink}>Register</Nav.Link>
                    </Nav>
                )}
                { isAuthenticated && (
                    <Nav style={{alignItems: "center"}}>
                        <Nav.Link to="/cart" as={NavLink} style={{ position: "relative" }} className="rounded-circle">
                            <Bag size={28}></Bag>
                            <div 
                                className="rounded-circle bg-danger d-flex justify-content-center align-items-center" 
                                id="amount-in-bag"
                                style={{
                                    color: "white",
                                    width: "1.25rem",
                                    height: "1.25rem",
                                    position: "absolute",
                                    bottom: 0,
                                    right: 0
                                }}
                            >{numInCart}</div>
                        </Nav.Link>
                        <Nav.Link to="/dashboard" as={NavLink}>Dashboard</Nav.Link>
                        <Nav.Link to="/dashboard" as={NavLink}>Logout</Nav.Link>
                    </Nav>
                )}
            </Container>
        </NavbarBs>
    );
};

export default Navbar;
