import React, { Fragment } from "react";

const CartItem = ({ cartItem }) => {

    const addToCart = async () => {
        const body = {
            "product_id": cartItem.product_id, 
            "product_size": cartItem.product_size, 
            "quantity": 1
        };
        const response = await fetch(`http://localhost:3001/cart/add-to-cart`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(body),
            credentials: "include"
        });

        if(response.ok) {
            const parseRes = await response.json();
            console.log(parseRes);
            alert("Item added successfully to your cart!");
        } else if(response.status === 403) {
            alert("You must be logged in to add items to your cart!");
        }
    };

    const removeFromCart = () => {

    };

    return (
        <Fragment>
            <div className="cart-item-container">
                <h3>{cartItem.product_name}</h3>
                <div className="product-size">
                    { cartItem.product_size === "S" && (
                        <p>Size: <span>Small</span></p>
                    )}
                    { cartItem.product_size === "M" && (
                        <p>Size: <span>Medium</span></p>
                    )}
                    { cartItem.product_size === "L" && (
                        <p>Size: <span>Large</span></p>
                    )}
                </div>
                <div className="quantity-container">
                    <button>-</button>
                    <input value={cartItem.quantity}/>
                    <button>+</button>
                </div>
                <h4>Price: $<span>{cartItem.total_price}</span></h4>
            </div>
        </Fragment>
    );
};

export default CartItem;
