import React, { Fragment, useEffect, useState } from "react";
import Breadcrumbs from '../../../components/Breadcrumbs/Breadcrumbs';
import OrderCard from "../../../components/OrderCard/OrderCard";
import './Orders.css';
import { Link } from "react-router-dom";

const Orders = () => {
    const [ordersType, setOrdersType] = useState('ready-completed');
    const [userOrders, setUserOrders] = useState([]);

    const changeOrdersType = (e) => {
        switch(e.target.text) {
            case "Orders":
                setOrdersType("ready-completed");
                break;
            case "Buy Again":
                setOrdersType("ordered-items");
                break;
            case "Not Ready Yet":
                setOrdersType("not-ready");
                break;      
            case "Cancelled Orders":
                setOrdersType("cancelled");
                break;
            default:
                break;                    
        }
    };

    const getUserOrders = async () => {
        const response = await fetch(`http://localhost:3001/orders/${ordersType}`, {
            method: "GET",
            credentials: "include"
        });
        if(response.ok) {
            const parseRes = await response.json();
            setUserOrders(parseRes);
        }
    };

    useEffect(() => {
        getUserOrders();
    }, [ordersType, getUserOrders]);

    return (
        <Fragment>
            <Breadcrumbs prevPage={"/dashboard"} prevPageName={"Dashboard"} curPageName={"Your Orders"} />
            <h1>Your Orders</h1>
            <div className="orders-page-tabs">
                <ul>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Orders</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Buy Again</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Not Ready Yet</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Cancelled Orders</Link></li>
                </ul>
            </div>
            { ordersType === "orders" && (
                <div className="order-cards-container">
                    {
                        userOrders.map(order => {
                            if(ordersType === "orders" && (order.order_status === "READY" || order.order_status === "COMPLETED")) {
                                return <OrderCard order={order} orderType={ordersType} />;
                            } else if(ordersType === "not-ready" && order.order_status === "IN_PROGRESS") {
                                return <OrderCard order={order} orderType={ordersType} />;
                            }
                        })
                    }
                </div>
            )}
            { ordersType === "not-ready" && (
                <div className="order-cards-container">
                    {
                        userOrders.map(order => {
                            if(order.order_status === "IN_PROGRESS") {
                                return <OrderCard order={order} orderType={ordersType} />;
                            }
                        })
                    }
                </div>
            )}
            { ordersType === "cancelled" && (
                <div className="order-cards-container">
                    {
                        userOrders.map(order => {
                            if(order.order_status === "CANCELLED") {
                                return <OrderCard order={order} orderType={ordersType} />;
                            }
                        })
                    }
                </div>
            )}
            { ordersType === "buy-again" && (
                <div className="item-cards-container">

                </div>
            )}
        </Fragment>
    );
};

export default Orders;
