import React, { Fragment, useEffect, useState } from "react";
import Breadcrumbs from '../../../components/Breadcrumbs/Breadcrumbs';

import './Orders.css';
import { Link } from "react-router-dom";

const Orders = () => {
    const [userOrders, setUserOrders] = useState([]);
    const [ordersType, setOrdersType] = useState("orders");

    const getUserOrders = async () => {
        const response = await fetch(`http://localhost:3001/orders`, {
            method: "GET",
            credentials: "include"
        });
        if(response.ok) {
            const parseRes = await response.json();
            setUserOrders(parseRes);
        }
    };

    const changeOrdersType = (e) => {
        switch(e.target.text) {
            case "Orders":
                setOrdersType("orders");
                break;
            case "Buy Again":
                setOrdersType("buy-again");
                break;
            case "Not Ready Yet":
                setOrdersType("not-ready");
                break;      
            case "Cancelled Orders":
                setOrdersType("cancelled");
                break;
            default:
                break;                    
        }
    };

    useEffect(() => {
        getUserOrders();
    });

    return (
        <Fragment>
            <Breadcrumbs prevPage={"/dashboard"} prevPageName={"Dashboard"} curPageName={"Your Orders"} />
            <h1>Your Orders</h1>
            <div className="orders-page-tabs">
                <ul>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Orders</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Buy Again</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Not Ready Yet</Link></li>
                    <li><Link to="/dashboard/orders" onClick={changeOrdersType}>Cancelled Orders</Link></li>
                </ul>
            </div>
            { ordersType !== "buy-again" && (
                <div className="order-cards-container">

                </div>
            )}
            { ordersType !== "buy-again" && (
                <div className="order-cards-container">

                </div>
            )}
        </Fragment>
    );
};

export default Orders;
