import React, { Fragment, useEffect, useState } from "react";
import MenuCategories from "./MenuCategories";
import './Menu.css';

const Menu = () => {
    const [numInCart, setNumInCart] = useState(0);

    useEffect(() => {
        const getNumInCart = async () => {
            const response = await fetch(`http://localhost:3001/cart`, {
                method: "GET",
                credentials: "include"
            });
            if(response.ok) {
                const parseRes = await response.json();
                let num = 0;
                for(let i = 0; i < parseRes.length; i++) {
                    num += parseRes[i].quantity;
                }
                setNumInCart(num);
                console.log(numInCart);
            }
        }
        getNumInCart();
    }, []);

    return (
        <Fragment>
            <h1>Menu</h1>
            <div className="menu-with-nav">
                <MenuCategories />
            </div>
            <span id="shopping-bag-icon" className="material-symbols-outlined">shopping_bag</span>
        </Fragment>
    );
}

export default Menu;
